    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        java.util.Date dataEmissao = jDateChooser1.getDate();
        java.util.Date dataVencimento = jDateChooser2.getDate();
        String situacao = jComboBox2.getSelectedItem().toString();

        Principal.despesa.setCodDesp(Integer.parseInt(jTextField2.getText()));
        Principal.despesa.setNFeDesp(jTextField4.getText());
        Principal.despesa.setSituacaoDesp(situacao);
        Principal.despesa.setNrSerieDesp(jTextField3.getText());
        Principal.despesa.setChaveNFeDesp(jTextField12.getText());
        Principal.despesa.setDataEmisDesp(dataEmissao);
        Principal.despesa.setDateVencDesp(dataVencimento);
        Principal.despesa.setValorDesp(Float.parseFloat(jTextField11.getText()));
        Principal.despesa.setNomeFornecedor(jTextField10.getText());
        Principal.despesa.setObservDesp(jTextArea1.getText());

        boolean cadastrou = Principal.despesaDao.inserirDespesa(Principal.despesa);
        if (cadastrou) {
            Alexa.escrevaJanela("Despesa cadastrada com sucesso!");
            limparCampos();
            jButton3.setEnabled(true);
        jButton4.setEnabled(true);
        } else {
            Alexa.escrevaJanela("Falha ao cadastrar Despesa!");
        }
    

    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        Principal.despesa.setCodDesp(Integer.parseInt(jTextField2.getText()));
        
        boolean inativar = Principal.despesaDao.inativarDesp(Principal.despesa.getCodDesp());
        if (inativar){
            Alexa.escrevaJanela("Despesa excluída com sucesso!");
        }else{
            Alexa.escrevaJanela("Falha ao excluir a Despesa!");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

