    public int gerarProximoIdDespesa() {
        int novoId = 1;

        String sqlSelect = "SELECT ultimo_id FROM gerador_id WHERE tabela = 'despesa' FOR UPDATE";
        String sqlUpdate = "UPDATE gerador_id SET ultimo_id = ? WHERE tabela = 'despesa'";

        try (Connection conn = DriverManager.getConnection(
                "jdbc:mysql://localhost:3306/crud?characterEncoding=utf-8", "root", "025507")) {

            conn.setAutoCommit(false); // Inicia transação

            try (PreparedStatement psSelect = conn.prepareStatement(sqlSelect); ResultSet rs = psSelect.executeQuery()) {

                if (rs.next()) {
                    novoId = rs.getInt("ultimo_id") + 1;
                }
            }

            try (PreparedStatement psUpdate = conn.prepareStatement(sqlUpdate)) {
                psUpdate.setInt(1, novoId);
                psUpdate.executeUpdate();
            }

            conn.commit();

        } catch (SQLException e) {
            e.printStackTrace();
        }

        return novoId;
    }

