    private void jButtonCadproActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonCadproActionPerformed
        // TODO add your handling code here:
        if (jNomepro.getText().matches("") || jValorpro.getText().matches("")) {
            Alexa.escrevaJanela("Preencha o campo NOME e o campo VALOR!");
        } else {
            Principal.procedimento.setIdProcedimento(Integer.parseInt(jCodpro.getText()));
            Principal.procedimento.setNomeProcedimento(jNomepro.getText());
            Principal.procedimento.setValorProcedimento(jValorpro.getText());

            boolean cadastrou = Principal.procedimentoDao.inserir(Principal.procedimento);
            if (cadastrou) {
                Alexa.escrevaJanela("Procedimento cadastrado com sucesso!");
                limparCampos();
                jCodpro.setEditable(true);
                jButtonBuscpro.setEnabled(true);
                jButtonAttpro.setEnabled(true);
                jButtonInapro.setEnabled(true);
            } else {
                Alexa.escrevaJanela("Falha ao cadastrar procedimento!");
            }
        }
    }//GEN-LAST:event_jButtonCadproActionPerformed

    private void jButtonAttproActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAttproActionPerformed
        // TODO add your handling code here:
        if (jNomepro.getText().matches("") || jValorpro.getText().matches("")) {
            Alexa.escrevaJanela("Preencha os campos NOME e VALOR!");
        } else {
            Principal.procedimento.setIdProcedimento(Integer.parseInt(jCodpro.getText()));
            Principal.procedimento.setNomeProcedimento(jNomepro.getText());
            Principal.procedimento.setValorProcedimento(jValorpro.getText());

            boolean cadastrou = Principal.procedimentoDao.atualizar(Principal.procedimento);
            if (cadastrou) {
                Alexa.escrevaJanela("Procedimento atualizado com sucesso!");
                limparCampos();
            } else {
                Alexa.escrevaJanela("Falha ao atualizar o procedimento!");
            }
        }
    }//GEN-LAST:event_jButtonAttproActionPerformed

    private void jButtonBuscproActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonBuscproActionPerformed
        // TODO add your handling code here:
        Principal.procedimento.setIdProcedimento(Integer.parseInt(jCodpro.getText()));
        Principal.procedimento = Principal.procedimentoDao.buscar(Principal.procedimento.getIdProcedimento());
        if (Principal.procedimento == null || Principal.procedimento.getNomeProcedimento() == null){
            Alexa.escrevaJanela("Nenhum prodecimento encontrado com este código!");
        }else{
            jNomepro.setText(Principal.procedimento.getNomeProcedimento());
            jValorpro.setText(Principal.procedimento.getValorProcedimento());
        }
    }//GEN-LAST:event_jButtonBuscproActionPerformed

    private void jButtonInaproActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonInaproActionPerformed
        // TODO add your handling code here:
        Principal.procedimento.setIdProcedimento(Integer.parseInt(jCodpro.getText()));
        boolean inativar = Principal.procedimentoDao.inativar(Principal.procedimento.getIdProcedimento());
        if (inativar) {
            Alexa.escrevaJanela("Procedimento inativado com sucesso");
        } else {
            Alexa.escrevaJanela("Procedimento não encontrado!");
        }
    }//GEN-LAST:event_jButtonInaproActionPerformed

