    private void jButtonNovoProActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonNovoProActionPerformed
        // TODO add your handling code here:
        int novoId = gerarProximoIdProcedimento();
        jCodpro.setText(String.valueOf(novoId));
        jCodpro.setEditable(false);
        jButtonBuscpro.setEnabled(false);
        jButtonAttpro.setEnabled(false);
        jButtonInapro.setEnabled(false);
        jNomepro.setText("");
        jValorpro.setText("");
    }//GEN-LAST:event_jButtonNovoProActionPerformed

